import { useState } from 'react'

const Button = (handleClick, text) => 
  <>
    <button onClick={handleClick}>
    text
    </button>
  </>

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const increaseState = (state, updateState) => updateState(state+1)

  return (
    <div>
      <h1> give Feedback </h1>
      <Button handleClick={increaseState(good, setGood)} text='good'/>
      <Button handleClick={increaseState(neutral, setNeutral)} text='neutral'/>
      <Button handleClick={increaseState(bad, setBad)} text='bad'/>
      <h1> Statistics </h1>
      good {good}
      neutral {neutral}
      bad {bad}
    </div>
  )
}

export default App
